INSERT INTO actualites(titre, description, contenu, categorie, source, url, image_url, date_publication, pays) VALUES
('Nouvelle politique économique en RDC','Le gouvernement annonce de nouvelles mesures économiques pour stimuler la croissance', 'Le gouvernement de la République Démocratique du Congo a dévoilé aujourd''hui un nouveau plan économique visant à stimuler la croissance et à attirer les investissements étrangers. Les mesures incluent des réformes fiscales et des incitations pour les entreprises.', 'politique', 'Radio Okapi', 'https://www.radiookapi.net', 'https://picsum.photos/400/300', NOW() - INTERVAL '1 hour', 'RDC'),
('Victoire des Léopards en match amical','L''équipe nationale de football remporte son match de préparation', 'Les Léopards de la RDC ont remporté une victoire convaincante 3-1 contre l''équipe du Ghana lors d''un match amical de préparation. Les buts ont été marqués par Mbayo, Bakambu et Mputu.', 'sport', 'Fédération de Football RDC', 'https://fecofa-rdc.com', 'https://picsum.photos/400/300', NOW() - INTERVAL '2 hours', 'RDC'),
('Festival culturel à Kinshasa','Grand festival culturel prévu ce weekend au Stade des Martyrs', 'La ville de Kinshasa accueillera ce weekend un grand festival culturel mettant en vedette des artistes locaux et internationaux. L''événement vise à promouvoir la richesse culturelle de la RDC.', 'culture', 'Ministère de la Culture', 'https://culture.gouv.cd', 'https://picsum.photos/400/300', NOW() - INTERVAL '3 hours', 'RDC');